import{_ as a,c as t,S as i,o as d}from"./chunks/framework.BKWZoghX.js";const g=JSON.parse('{"title":"Changelog","description":"","frontmatter":{},"headers":[],"relativePath":"changelog.md","filePath":"changelog.md","lastUpdated":1763718236000}'),o={name:"changelog.md"};function l(r,e,s,n,c,h){return d(),t("div",null,e[0]||(e[0]=[i('<h1 id="changelog" tabindex="-1">Changelog <a class="header-anchor" href="#changelog" aria-label="Permalink to &quot;Changelog&quot;">​</a></h1><p>All notable changes to this project will be documented in this file.</p><p>The format is based on Keep a Changelog, and this project adheres to Semantic Versioning.</p><h2 id="_0-2-0-2025-11-07" tabindex="-1">[0.2.0] - 2025-11-07 <a class="header-anchor" href="#_0-2-0-2025-11-07" aria-label="Permalink to &quot;[0.2.0] - 2025-11-07&quot;">​</a></h2><h3 id="added" tabindex="-1">Added <a class="header-anchor" href="#added" aria-label="Permalink to &quot;Added&quot;">​</a></h3><ul><li>[dataset] Datasets now hosted on HuggingFace Hub for easier access and distribution.</li><li>[dataset] Support for standard HuggingFace <code>datasets</code> library for data loading.</li><li>[docs] Interactive dataset configurator with dynamic channel discovery from HuggingFace API.</li><li>[docs] Updated documentation site with HuggingFace integration examples.</li></ul><h3 id="changed" tabindex="-1">Changed <a class="header-anchor" href="#changed" aria-label="Permalink to &quot;Changed&quot;">​</a></h3><ul><li>[dataset] Migrated from NERSC manifest-based distribution to HuggingFace datasets.</li><li>[dataset] Data now stored in Parquet format with improved compression and accessibility.</li><li>[docs] Simplified data access workflow using <code>load_dataset()</code> instead of custom CLI.</li></ul><h3 id="removed" tabindex="-1">Removed <a class="header-anchor" href="#removed" aria-label="Permalink to &quot;Removed&quot;">​</a></h3><ul><li>[docs] Removed NERSC manifest.json dependency from documentation build process.</li></ul><h2 id="_0-1-0-2025-09-08" tabindex="-1">[0.1.0] - 2025-09-08 <a class="header-anchor" href="#_0-1-0-2025-09-08" aria-label="Permalink to &quot;[0.1.0] - 2025-09-08&quot;">​</a></h2><h3 id="added-1" tabindex="-1">Added <a class="header-anchor" href="#added-1" aria-label="Permalink to &quot;Added&quot;">​</a></h3><ul><li>[dataset] Initial release of ColliderML dataset with ttbar and ggf physics processes.</li><li>[dataset] Four detector hierarchy levels: particles, tracker_hits, calo_hits, and tracks.</li><li>[dataset] Approximately 100,000 simulated events per process with no pileup (pu0).</li><li>[library] Initial <code>colliderml</code> Python library with data access utilities.</li><li>[docs] Documentation website with VitePress framework.</li><li>[docs] Dataset configuration modal for exploring available data.</li></ul><hr><p>Unreleased changes should be added under a <code>## [Unreleased]</code> header above with entries marked as <code>[dataset]</code>, <code>[library]</code>, or <code>[docs]</code>.</p>',15)]))}const m=a(o,[["render",l]]);export{g as __pageData,m as default};
