import{_ as e,c as t,S as l,o as a}from"./chunks/framework.DegPjLFS.js";const g=JSON.parse('{"title":"Introduction to ColliderML","description":"","frontmatter":{},"headers":[],"relativePath":"guide/introduction.md","filePath":"guide/introduction.md","lastUpdated":1757288386000}'),r={name:"guide/introduction.md"};function n(o,i,s,d,c,p){return a(),t("div",null,i[0]||(i[0]=[l('<h1 id="introduction-to-colliderml" tabindex="-1">Introduction to ColliderML <a class="header-anchor" href="#introduction-to-colliderml" aria-label="Permalink to &quot;Introduction to ColliderML&quot;">​</a></h1><p>ColliderML is a modern machine learning library designed specifically for high-energy physics (HEP) data analysis. It provides efficient tools for accessing, processing, and analyzing large-scale particle physics datasets.</p><h2 id="why-colliderml" tabindex="-1">Why ColliderML? <a class="header-anchor" href="#why-colliderml" aria-label="Permalink to &quot;Why ColliderML?&quot;">​</a></h2><p>High-energy physics data analysis presents unique challenges:</p><ul><li>Large-scale datasets distributed across multiple locations</li><li>Complex data formats specific to particle physics</li><li>Need for efficient parallel processing</li><li>Requirements for data integrity and verification</li></ul><p>ColliderML addresses these challenges by providing:</p><ol><li><p><strong>Efficient Data Access</strong></p><ul><li>Parallel downloading capabilities</li><li>Resume functionality for interrupted transfers</li><li>Automatic retries with exponential backoff</li><li>Progress tracking and detailed status reporting</li></ul></li><li><p><strong>HEP Data Support</strong></p><ul><li>Native support for ROOT files</li><li>Integration with XRootD for CERN data access</li><li>Unified interface for various HEP data formats</li></ul></li><li><p><strong>Machine Learning Integration</strong></p><ul><li>Specialized utilities for particle physics</li><li>Easy integration with popular ML frameworks</li><li>Tools for dataset preparation and preprocessing</li></ul></li><li><p><strong>Visualization Tools</strong></p><ul><li>Interactive data exploration</li><li>Physics-specific visualizations</li><li>Analysis result plotting</li></ul></li></ol><h2 id="core-design-principles" tabindex="-1">Core Design Principles <a class="header-anchor" href="#core-design-principles" aria-label="Permalink to &quot;Core Design Principles&quot;">​</a></h2><p>ColliderML is built on several key principles:</p><ul><li><strong>Performance</strong>: Optimized for handling large-scale physics data</li><li><strong>Reliability</strong>: Robust error handling and data integrity checks</li><li><strong>Usability</strong>: Clean, intuitive API design</li><li><strong>Extensibility</strong>: Easy to extend and customize</li></ul><h2 id="next-steps" tabindex="-1">Next Steps <a class="header-anchor" href="#next-steps" aria-label="Permalink to &quot;Next Steps&quot;">​</a></h2><ul><li><a href="./installation.html">Installation Guide</a> - Get ColliderML up and running</li><li><a href="./quickstart.html">Quick Start</a> - Start using ColliderML in minutes</li><li><a href="./data-management.html">Core Concepts</a> - Learn about the fundamental concepts</li></ul>',12)]))}const h=e(r,[["render",n]]);export{g as __pageData,h as default};
